import React, { useState } from "react";
import axios from 'axios';
import FileUpload from "../File/File.component";
import './Text-Area.styles.css';

function TextArea() {
    const [text, setText] = useState("");

    const handleTextAreaChange = async (e) => {
        setText(e.target.value);
    };

    async function compileAndDeploy() {
        const params = {
            contractName: 'Storage',
            sourceCode: text,
            network: 'Something'
        }
        try {
            console.log("At text area");
            const response = await axios.get('http://10.1.33.124:8000/compile-and-deploy', { params });
            console.log(response.data);
            if (response.data.result.abi) {
                window.alert('Compiled successfully');
            }
            else {
                window.alert(response.data.result);
            }
            // Find the constructor in the ABI
            let args = [];
            let numInputs = 0;
            const abi = response.data.result.abi;
            const bytecode = response.data.result.bytecode;
            const constructor = abi.find(item => item.type === 'constructor');
            console.log('Constructor Inputs', numInputs);
            if (constructor) {
                // Access constructor parameters and their types
                const constructorInputs = constructor.inputs || [];
                console.log('Constructor Parameters:');
                constructorInputs.forEach(param => {
                    console.log(`Name: ${param.name}, Type: ${param.type}`);
                });
                numInputs = constructor.inputs.length;
            } else {
                console.log('No constructor found in the ABI.');
            }
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    }

    return (
        <div className="text-area">
            <h4>Paste your code below:</h4>
            <textarea
                className="inner"
                id="large-text"
                name="large-text"
                rows="25"
                cols="70"
                value={text}
                onChange={handleTextAreaChange}
            ></textarea>
            <br />
            <h4>Or upload the code file here:</h4>
            <FileUpload></FileUpload>
            <button className="compile-button" onClick={compileAndDeploy}> Compile & Deploy </button>
        </div>
    );
}

export default TextArea;
