import React, { useState } from "react";
import axios from 'axios';
import FileUpload from "../File/File.component";
import './Text-Area.styles.css';

function TextArea() {
    const [text, setText] = useState("");

    const handleTextAreaChange = async (e) => {
        setText(e.target.value);
        const params = {
            contractName: 'Storage',
            sourceCode: text,
            network: 'Something'
        }
        try {
            console.log("At text area");
            const response = await axios.get('http://10.1.33.124:8000/compile-and-deploy', { params });
            console.log(response.data);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    return (
        <div className="text-area">
            <h4>Paste your code below:</h4>
            <textarea
                className="inner"
                id="large-text"
                name="large-text"
                rows="25"
                cols="70"
                value={text}
                onChange={handleTextAreaChange}
            ></textarea>
            <br />
            <h4>Or upload the code file here:</h4>
            <FileUpload></FileUpload>
        </div>
    );
}

export default TextArea;
