        const newInputValues = [...inputValues];
        if (constructorInputs[index].type === 'uint256') {
            console.log('Number');
            newInputValues[index] = Number(value);
        }
        else {
            console.log(constructorInputs);
            newInputValues[index] = value;
        }
        setInputValues(newInputValues);
