        const params = {
            contractName: 'Storage',
            sourceCode: text,
            network: 'Something'
        };
        try {
            console.log("At text area");
            const response = await axios.get('http://10.1.33.124:8000/compile-and-deploy', { params });
            console.log(response.data);
            if (response.data.result.abi) {
